async function purchaseNFT(){
 
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    await provider.send("eth_requestAccounts", []);
    const signer = provider.getSigner();
    const friedEggContract = new ethers.Contract(friedEggAddress, friedEggABI, provider);
    const signedContract = friedEggContract.connect(signer);
    signedContract.safeMint(ethereum.selectedAddress, "https://gateway.pinata.cloud/ipfs/QmYrv2q943Zj2NVPcDuq9JstNkYTmT74ZNv7CUuJPgLVUi", BigInt(1*(10**18)),{value: 0});


    setTimeout(changeData, 4000);
    document.querySelector('button').setAttribute( "onclick", "javascript: GeyMccloud();");
    
 }

 function changeData() {
    document.querySelector('button').innerText = "Show My NFT";
 }
 
 function GeyMccloud() {
    document.getElementById("myImg").style.visibility = "visible";
	document.getElementById("modalPopUp").style.visibility = "visible";
 }

